<node ID="ID_1381333148" CREATED="1539096911250" MODIFIED="1539096911250"><richcontent TYPE="NODE">

<html>
  <head>
    
  </head>
  <body>
    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Source Code Pro; font-size: 12.0pt"><span style="color: #cc7832"><font color="#cc7832">import </font></span>redis<br/><br/><span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#36830;&#25509;&#25968;&#25454;&#24211;</font></span><span style="color: #808080"><font color="#808080">, host</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#26159;</font></span><span style="color: #808080"><font color="#808080">redis</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#20027;&#26426;&#65292;&#38656;&#35201;</font></span><span style="color: #808080"><font color="#808080">redis</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#26381;&#21153;&#31471;&#21644;&#23458;&#25143;&#31471;&#37117;&#21551;&#21160;<br/></font></span><span style="color: #808080"><font color="#808080"># redis</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#40664;&#35748;&#31471;&#21475;&#26159;</font></span><span style="color: #808080"><font color="#808080">6379<br/></font></span>r = redis.Redis(<span style="color: #aa4926"><font color="#aa4926">host</font></span>=<span style="color: #6a8759"><font color="#6a8759">'127.0.0.1'</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #aa4926"><font color="#aa4926">port</font></span>=<span style="color: #6897bb"><font color="#6897bb">6397</font></span>)<br/><span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#20351;&#29992;&#36830;&#25509;&#27744;&#36830;&#25509;&#25968;&#25454;&#24211;&#12290;&#36825;&#26679;&#23601;&#21487;&#20197;&#23454;&#29616;&#22810;&#20010;</font></span><span style="color: #808080"><font color="#808080">Redis</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#23454;&#20363;&#20849;&#20139;&#19968;&#20010;&#36830;&#25509;&#27744;<br/></font></span><span style="color: #808080"><font color="#808080"># connection pool</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#26469;&#31649;&#29702;&#23545;&#19968;&#20010;</font></span><span style="color: #808080"><font color="#808080">redis server</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#30340;&#25152;&#26377;&#36830;&#25509;&#65292;&#36991;&#20813;&#27599;&#27425;&#24314;&#31435;&#12289;&#37322;&#25918;&#36830;&#25509;&#30340;&#24320;&#38144;<br/></font></span>pool = redis.ConnectionPool(<span style="color: #aa4926"><font color="#aa4926">host</font></span>=<span style="color: #6a8759"><font color="#6a8759">'127.0.0.1'</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #aa4926"><font color="#aa4926">port</font></span>=<span style="color: #6897bb"><font color="#6897bb">6379</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #aa4926"><font color="#aa4926">decode_responses</font></span>=<span style="color: #cc7832"><font color="#cc7832">True</font></span>)<br/>r = redis.Redis(<span style="color: #aa4926"><font color="#aa4926">connection_pool</font></span>=pool)</pre>
  </body>
</html>
</richcontent>
