    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Source Code Pro; font-size: 12.0pt"><span style="color: #cc7832"><font color="#cc7832">from </font></span>flask <span style="color: #cc7832"><font color="#cc7832">import </font></span>Flask<span style="color: #cc7832"><font color="#cc7832">, </font></span>render_template<br/><span style="color: #cc7832"><font color="#cc7832">from </font></span>flask_socketio <span style="color: #cc7832"><font color="#cc7832">import </font></span>SocketIO<br/><br/>app = Flask(__name__)<br/>app.config[<span style="color: #6a8759"><font color="#6a8759">'SECRET_KEY'</font></span>] = <span style="color: #6a8759"><font color="#6a8759">'secret!'<br/></font></span>socketio = SocketIO(app)<br/><br/><span style="color: #cc7832"><font color="#cc7832">if </font></span>__name__ == <span style="color: #6a8759"><font color="#6a8759">'__main__'</font></span>:<br/>    socketio.run(app)</pre>
