ont-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#24320;&#21551;&#27515;&#24490;&#29615;&#31561;&#24453;&#23458;&#25143;&#31471;&#36830;&#25509;<br/></font></span><span style="color: #cc7832"><font color="#cc7832">while True</font></span>:<br/>    <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">'</font></span><span style="color: #6a8759; font-family: &#x5b8b;&#x4f53;"><font color="#6a8759" face="&#x5b8b;&#x4f53;">&#26381;&#21153;&#22120;&#21551;&#21160;</font></span><span style="color: #6a8759"><font color="#6a8759">...'</font></span>)<br/>    <span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#31561;&#24453;&#38142;&#25509;</font></span><span style="color: #808080"><font color="#808080">,</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#38459;&#22622;&#65292;&#30452;&#21040;&#28192;&#36947;&#38142;&#25509;</font></span><span style="color: #808080"><font color="#808080"> conn</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#25171;&#24320;&#19968;&#20010;&#26032;&#30340;&#23545;&#35937;&#19987;&#38376;&#32473;&#24403;&#21069;&#38142;&#25509;&#30340;&#23458;&#25143;&#31471;</font></span><span style="color: #808080"><font color="#808080"> addr</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#26159;</font></span><span style="color: #808080"><font color="#808080">ip</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#22320;&#22336;<br/></font></span><font color="#808080" face="&#x5b8b;&#x4f53;"><span style="color: #808080; font-family: &#x5b8b;&#x4f53;">    </span></font>conn<span style="color: #cc7832"><font color="#cc7832">, </font></span>addr = sk.accept()<br/>    <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;</font></span><span style="color: #6a8759; font-family: &#x5b8b;&#x4f53;"><font color="#6a8759" face="&#x5b8b;&#x4f53;">&#23458;&#25143;&#31471;&#22320;&#22336;</font></span><span style="color: #6a8759"><font color="#6a8759">:&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #8888c6"><font color="#8888c6">str</font></span>(addr))<br/><br/>    <span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#33719;&#21462;&#23458;&#25143;&#31471;&#35831;&#27714;&#25968;&#25454;<br/></font></span><font color="#808080" face="&#x5b8b;&#x4f53;"><span style="color: #808080; font-family: &#x5b8b;&#x4f53;">    </span></font><span style="color: #808080"><font color="#808080"># client_data = conn.recv(1024)<br/></font></span><font color="#808080"><span style="color: #808080">    </span></font>client_data = client_d()<br/><br/>    <span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#35299;&#30721;&#25171;&#21360;&#23545;&#26041;&#30340;&#25968;&#25454;<br/></font></span><font color="#808080" face="&#x5b8b;&#x4f53;"><span style="color: #808080; font-family: &#x5b8b;&#x4f53;">    </span></font><span style="color: #8888c6"><font color="#8888c6">print</font></span>(client_data.decode(<span style="color: #6a8759"><font color="#6a8759">&quot;utf-8&quot;</font></span>))<br/><br/>    <span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#21521;&#23545;&#26041;&#21457;&#36865;&#25968;&#25454;<br/></font></span><font color="#808080" face="&#x5b8b;&#x4f53;"><span style="color: #808080; font-family: &#x5b8b;&#x4f53;">    </span></font><span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#20063;&#21487;&#20889;&#25104;</font></span><span style="color: #808080"><font color="#808080">conn.send(&quot;</font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#26469;&#33258;&#26381;&#21153;&#22120;&#30340;&#38382;&#20505;</font></span><span style="color: #808080"><font color="#808080">&quot;.encode(&quot;utf-8&quot;))<br/></font></span><font color="#808080"><span style="color: #808080">    </span></font>conn.send(<span style="color: #8888c6"><font color="#8888c6">bytes</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;HTTP/1.1 200 OK</font></span><span style="color: #cc7832"><font color="#cc7832">\r\n\r\n</font></span><span style="color: #6a8759"><font color="#6a8759">&lt;h1&gt;</font></span><span style="color: #6a8759; font-family: &#x5b8b;&#x4f53;"><font color="#6a8759" face="&#x5b8b;&#x4f53;">&#26469;&#33258;&#26381;&#21153;&#22120;&#30340;&#38382;&#20505;</font></span><span style="color: #6a8759"><font color="#6a8759">&lt;/h1&gt;&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #6a8759"><font color="#6a8759">&quot;utf-8&quot;</font></span>))<br/>    <span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: &#x5b8b;&#x4f53;"><font color="#808080" face="&#x5b8b;&#x4f53;">&#20851;&#38381;&#38142;&#25509;<br/></font></span><font color="#808080" face="&#x5b8b;&#x4f53;"><span style="color: #808080; font-family: &#x5b8b;&#x4f53;">    </span></font>conn.close()</pre>
  </body>
