    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Consolas; font-size: 9.8pt"><span style="color: #cc7832"><font color="#cc7832">void </font></span><span style="color: #ffc66d"><font color="#ffc66d">reverse</font></span>(<span style="color: #cc7832"><font color="#cc7832">int</font></span>[] nums) {<br/>    <span style="color: #cc7832"><font color="#cc7832">int </font></span>left <span style="color: #cc7832"><font color="#cc7832">= </font></span><span style="color: #6897bb; font-style: italic"><font color="#6897bb"><i>0</i></font></span>;<br/>    <span style="color: #cc7832"><font color="#cc7832">int </font></span>right <span style="color: #cc7832"><font color="#cc7832">= </font></span>nums.length <span style="color: #cc7832"><font color="#cc7832">- </font></span><span style="color: #6897bb; font-style: italic"><font color="#6897bb"><i>1</i></font></span>;<br/>    <span style="color: #cc7832"><font color="#cc7832">while </font></span>(left <span style="color: #cc7832"><font color="#cc7832">&lt;</font></span> right) {<br/>        <span style="color: #808080"><font color="#808080">// swap(nums[left], nums[right])<br/></font></span><font color="#808080"><span style="color: #808080">        </span></font><span style="color: #cc7832"><font color="#cc7832">int </font></span>temp <span style="color: #cc7832"><font color="#cc7832">=</font></span> nums[left];<br/>        nums[left] <span style="color: #cc7832"><font color="#cc7832">=</font></span> nums[right];<br/>        nums[right] <span style="color: #cc7832"><font color="#cc7832">=</font></span> temp;<br/>        left<span style="color: #cc7832"><font color="#cc7832">++</font></span>; right<span style="color: #cc7832"><font color="#cc7832">--</font></span>;<br/>    }<br/>}</pre>
