<node ID="ID_1415331613" CREATED="1597920216982" MODIFIED="1597920216982"><richcontent TYPE="NODE">

<html>
  <head>
    
  </head>
  <body>
    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: JetBrains Mono,monospace; font-size: 9.8pt"><span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">solve_n_queens</font></span>(n):<br/>    res = []<br/>    board = [[<span style="color: #6a8759"><font color="#6a8759">"." </font></span><span style="color: #cc7832"><font color="#cc7832">for </font></span>_ <span style="color: #cc7832"><font color="#cc7832">in </font></span><span style="color: #8888c6"><font color="#8888c6">range</font></span>(n)] <span style="color: #cc7832"><font color="#cc7832">for </font></span>_ <span style="color: #cc7832"><font color="#cc7832">in </font></span><span style="color: #8888c6"><font color="#8888c6">range</font></span>(n)]<br/><br/>    <span style="color: #808080"><font color="#808080"># i </font></span><span style="color: #808080; font-family: 宋体,monospace"><font color="#808080" face="宋体,monospace">行，</font></span><span style="color: #808080"><font color="#808080"> j </font></span><span style="color: #808080; font-family: 宋体,monospace"><font color="#808080" face="宋体,monospace">列<br/></font></span><font color="#808080" face="宋体,monospace"><span style="color: #808080; font-family: 宋体,monospace">    </span></font><span style="color: #808080"><font color="#808080"># </font></span><span style="color: #808080; font-family: 宋体,monospace"><font color="#808080" face="宋体,monospace">同一左对角线的坐标</font></span><span style="color: #808080"><font color="#808080">(i, j), i1 - j1 == i2 - j2<br/></font></span><font color="#808080"><span style="color: #808080">    # </span></font><span style="color: #808080; font-family: 宋体,monospace"><font color="#808080" face="宋体,monospace">同一右对角线的坐标</font></span><span style="color: #808080"><font color="#808080">(i, j), i1 + j1 == i2 + j2<br/></font></span><font color="#808080"><span style="color: #808080">    </span></font><span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">helper</font></span>(left_diagonal<span style="color: #cc7832"><font color="#cc7832">, </font></span>right_diagonal<span style="color: #cc7832"><font color="#cc7832">, </font></span>column):<br/>        <span style="color: #cc7832"><font color="#cc7832">if </font></span>n == <span style="color: #8888c6"><font color="#8888c6">len</font></span>(column):<br/>            temp = []<br/>            <span style="color: #cc7832"><font color="#cc7832">for </font></span>line <span style="color: #cc7832"><font color="#cc7832">in </font></span>board:<br/>                temp.append(<span style="color: #6a8759"><font color="#6a8759">""</font></span>.join(line))<br/>            <span style="color: #8888c6"><font color="#8888c6">print</font></span>(temp)<br/>            res.append(temp)<br/><br/>        j = <span style="color: #8888c6"><font color="#8888c6">len</font></span>(column)<br/>        <span style="color: #cc7832"><font color="#cc7832">for </font></span>i <span style="color: #cc7832"><font color="#cc7832">in </font></span><span style="color: #8888c6"><font color="#8888c6">range</font></span>(n):<br/>            <span style="color: #cc7832"><font color="#cc7832">if </font></span>i <span style="color: #cc7832"><font color="#cc7832">in </font></span>column:<br/>                <span style="color: #cc7832"><font color="#cc7832">continue<br/></font></span><font color="#cc7832"><span style="color: #cc7832">            if </span></font>i - j <span style="color: #cc7832"><font color="#cc7832">in </font></span>left_diagonal <span style="color: #cc7832"><font color="#cc7832">or </font></span>i + j <span style="color: #cc7832"><font color="#cc7832">in </font></span>right_diagonal:<br/>                <span style="color: #cc7832"><font color="#cc7832">continue<br/></font></span><font color="#cc7832"><span style="color: #cc7832">            </span></font>board[j][i] = <span style="color: #6a8759"><font color="#6a8759">"Q"<br/></font></span><font color="#6a8759"><span style="color: #6a8759">            </span></font>helper(left_diagonal + [i - j]<span style="color: #cc7832"><font color="#cc7832">, </font></span>right_diagonal + [i + j]<span style="color: #cc7832"><font color="#cc7832">, </font></span>column + [i])<br/>            board[j][i] = <span style="color: #6a8759"><font color="#6a8759">"."<br/></font></span><font color="#6a8759"><span style="color: #6a8759"><br/></span><span style="color: #6a8759">    </span></font>helper(<span style="color: #aa4926"><font color="#aa4926">left_diagonal</font></span>=[]<span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #aa4926"><font color="#aa4926">right_diagonal</font></span>=[]<span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #aa4926"><font color="#aa4926">column</font></span>=[])<br/>    <span style="color: #cc7832"><font color="#cc7832">return </font></span>res<br/></pre>
  </body>
</html>
</richcontent>
