      <div>
        <font color="#4ec9b0">TreeNode</font>&nbsp;<font color="#dcdcaa">insertIntoBST</font>(<font color="#4ec9b0">TreeNode</font>&nbsp;root,&nbsp;<font color="#4ec9b0">int</font>&nbsp;val)&nbsp;{
      </div>
      <div>
        &nbsp;&nbsp;&nbsp;&nbsp;<font color="#6a9955">//&nbsp;找到空位置插入新节点</font>
      </div>
      <div>
        &nbsp;&nbsp;&nbsp;&nbsp;<font color="#c586c0">if</font>&nbsp;(root&nbsp;==&nbsp;<font color="#569cd6">null</font>)&nbsp;<font color="#c586c0">return</font>&nbsp;<font color="#c586c0">new</font>&nbsp;<font color="#dcdcaa">TreeNode</font>(val);
      </div>
      <div>
        &nbsp;&nbsp;&nbsp;&nbsp;<font color="#6a9955">//&nbsp;if&nbsp;(root.val&nbsp;==&nbsp;val)</font>
      </div>
      <div>
        &nbsp;&nbsp;&nbsp;&nbsp;<font color="#6a9955">//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;BST&nbsp;中一般不会插入已存在元素</font>
      </div>
