        &nbsp;&nbsp;&nbsp;&nbsp;<font color="#c586c0">if</font>&nbsp;(<font color="#9cdcfe">root</font>.<font color="#9cdcfe">val</font>&nbsp;&gt;&nbsp;val)&nbsp;
      </div>
      <div>
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font color="#9cdcfe">root</font>.<font color="#9cdcfe">left</font>&nbsp;=&nbsp;<font color="#dcdcaa">insertIntoBST</font>(<font color="#9cdcfe">root</font>.<font color="#9cdcfe">left</font>,&nbsp;val);
      </div>
      <div>
        &nbsp;&nbsp;&nbsp;&nbsp;<font color="#c586c0">return</font>&nbsp;root;
