    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: JetBrains Mono,monospace; font-size: 9.8pt"><span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">serialize</font></span>(root):<br/>    <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">traverse</font></span>(node):<br/>        <span style="color: #cc7832"><font color="#cc7832">if </font></span>node <span style="color: #cc7832"><font color="#cc7832">is not None</font></span>:<br/>            res.append(<span style="color: #8888c6"><font color="#8888c6">str</font></span>(node.val))<br/>            traverse(node.left)<br/>            traverse(node.right)<br/>        <span style="color: #cc7832"><font color="#cc7832">else</font></span>:<br/>            res.append(<span style="color: #6a8759"><font color="#6a8759">"None"</font></span>)<br/>    res = []<br/>    traverse(root)<br/>    <span style="color: #cc7832"><font color="#cc7832">return </font></span><span style="color: #6a8759"><font color="#6a8759">","</font></span>.join(res)</pre>
