<node TEXT="结构基础" ID="ID_1232178553" CREATED="1597929379891" MODIFIED="1597929385445">
<node TEXT="数组（顺序存储）" ID="ID_780208225" CREATED="1597929275907" MODIFIED="1597929286477">
<node TEXT="优点" ID="ID_1758307834" CREATED="1597929893257" MODIFIED="1597929896260">
<node TEXT=" 紧凑连续存储,相对节约存储空间" ID="ID_1700413423" CREATED="1598011412846" MODIFIED="1598011436382"/>
<node TEXT=" 可以随机访问" ID="ID_561923168" CREATED="1598011420186" MODIFIED="1598011424976"/>
<node TEXT=" 通过索引快速找到对应元素" ID="ID_1544077687" CREATED="1598011426010" MODIFIED="1598011427236"/>
</node>
<node TEXT="缺点" ID="ID_563332414" CREATED="1597929897098" MODIFIED="1597929902292">
<node TEXT=" 因为连续存储，内存空间必须一次性分配够" ID="ID_1252827527" CREATED="1598011461866" MODIFIED="1598011465053"/>
<node TEXT=" 数组如果要扩容，需要重新分配一块更大的空间，再把数据全部复制过去，时间复杂度 O(N)" ID="ID_1108181767" CREATED="1598011474362" MODIFIED="1598011475500"/>
<node TEXT=" 在数组中间进行插入和删除，每次必须搬移后面的所有数据以保持连续，时间复杂度 O(N)" ID="ID_1705259837" CREATED="1598011490019" MODIFIED="1598011491608"/>
</node>
</node>
<node TEXT="链表（链式存储）" ID="ID_713933711" CREATED="1597929286867" MODIFIED="1597929297405">
<node TEXT="优点" ID="ID_34606795" CREATED="1597929904506" MODIFIED="1597929906724">
<node TEXT=" 元素不连续，而是靠指针指向下一个元素的位置，所以不存在数组的扩容问题" ID="ID_928247396" CREATED="1598011628553" MODIFIED="1598011629946"/>
<node TEXT=" 如果知道某一元素的前驱和后驱，操作指针即可删除该元素或者插入新元素，时间复杂度 O(1)" ID="ID_542878785" CREATED="1598011831135" MODIFIED="1598011832882"/>
</node>
<node TEXT="缺点" ID="ID_514836712" CREATED="1597929906905" MODIFIED="1597929909219">
<node TEXT=" 因为存储空间不连续，你无法根据一个索引算出对应元素的地址，所以不能随机访问" ID="ID_541557549" CREATED="1598011856567" MODIFIED="1598011857982"/>
<node TEXT=" 由于每个元素必须存储指向前后元素位置的指针，会消耗相对更多的储存空间" ID="ID_306145037" CREATED="1598011868799" MODIFIED="1598011870258"/>
</node>
</node>
</node>
<node TEXT="上层建筑" ID="ID_497438043" CREATED="1597929369946" MODIFIED="1597929378937">
