<node ID="ID_732283610" CREATED="1601281022552" MODIFIED="1601281022552"><richcontent TYPE="NODE">

<html>
  <head>
    
  </head>
  <body>
    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: JetBrains Mono,monospace; font-size: 9.8pt"><span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">counting_sort</font></span>(nums):<br/>    max_n = <span style="color: #8888c6"><font color="#8888c6">max</font></span>(nums)<br/>    min_n = <span style="color: #8888c6"><font color="#8888c6">min</font></span>(nums)<br/>    bucket_length = max_n - min_n + <span style="color: #6897bb"><font color="#6897bb">1<br/></font></span><font color="#6897bb"><span style="color: #6897bb">    </span></font>bucket = [<span style="color: #6897bb"><font color="#6897bb">0</font></span>] * bucket_length<br/>    <span style="color: #cc7832"><font color="#cc7832">for </font></span>num <span style="color: #cc7832"><font color="#cc7832">in </font></span>nums:<br/>        i = num - min_n<br/>        bucket[i] += <span style="color: #6897bb"><font color="#6897bb">1<br/></font></span><font color="#6897bb"><span style="color: #6897bb">    </span></font>nums.clear()<br/>    <span style="color: #cc7832"><font color="#cc7832">for </font></span>i<span style="color: #cc7832"><font color="#cc7832">, </font></span>count <span style="color: #cc7832"><font color="#cc7832">in </font></span><span style="color: #8888c6"><font color="#8888c6">enumerate</font></span>(bucket):<br/>        nums.extend([min_n + i <span style="color: #cc7832"><font color="#cc7832">for </font></span>_ <span style="color: #cc7832"><font color="#cc7832">in </font></span><span style="color: #8888c6"><font color="#8888c6">range</font></span>(count)])<br/>    <span style="color: #cc7832"><font color="#cc7832">return </font></span>nums</pre>
  </body>
</html>
</richcontent>
