<node ID="ID_1911756286" CREATED="1533562838544" MODIFIED="1533562838544"><richcontent TYPE="NODE">

<html>
  <head>
    
  </head>
  <body>
    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Source Code Pro; font-size: 10.5pt"><span style="color: #cc7832"><font color="#cc7832">class </font></span>Data_test(<span style="color: #8888c6"><font color="#8888c6">object</font></span>):<br/>    day = <span style="color: #6897bb"><font color="#6897bb">0<br/></font></span><font color="#6897bb"><span style="color: #6897bb">    </span></font>month = <span style="color: #6897bb"><font color="#6897bb">0<br/></font></span><font color="#6897bb"><span style="color: #6897bb">    </span></font>year = <span style="color: #6897bb"><font color="#6897bb">0<br/></font></span><font color="#6897bb"><span style="color: #6897bb">    </span></font><span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #b200b2"><font color="#b200b2">__init__</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>year=<span style="color: #6897bb"><font color="#6897bb">0</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>month=<span style="color: #6897bb"><font color="#6897bb">0</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>day = <span style="color: #6897bb"><font color="#6897bb">0</font></span>):<br/>        <span style="color: #94558d"><font color="#94558d">self</font></span>.year = year<br/>        <span style="color: #94558d"><font color="#94558d">self</font></span>.month = month<br/>        <span style="color: #94558d"><font color="#94558d">self</font></span>.day = day<br/><br/>    <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">out_data</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span>):<br/>        <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">f&quot;year:{self.year}&quot;</font></span>)<br/>        <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">f&quot;month:{self.month}&quot;</font></span>)<br/>        <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">f&quot;day:{self.day}&quot;</font></span>)<br/><br/>    <span style="color: #bbb529"><font color="#bbb529">@classmethod<br/></font></span><font color="#bbb529"><span style="color: #bbb529">    </span></font><span style="color: #6a8759"><font color="#6a8759">&quot;&quot;&quot;</font></span><span style="color: #6a8759; font-family: &#x5b8b;&#x4f53;"><font color="#6a8759" face="&#x5b8b;&#x4f53;">&#23558;</font></span><span style="color: #6a8759"><font color="#6a8759">'2018-7-6'</font></span><span style="color: #6a8759; font-family: &#x5b8b;&#x4f53;"><font color="#6a8759" face="&#x5b8b;&#x4f53;">&#36716;&#21270;&#20026;</font></span><span style="color: #6a8759"><font color="#6a8759">__init__</font></span><span style="color: #6a8759; font-family: &#x5b8b;&#x4f53;"><font color="#6a8759" face="&#x5b8b;&#x4f53;">&#21487;&#29992;&#30340;&#21442;&#25968;</font></span><span style="color: #6a8759"><font color="#6a8759">&quot;&quot;&quot;<br/></font></span><font color="#6a8759"><span style="color: #6a8759">    </span></font><span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">get_data</font></span>(<span style="color: #94558d"><font color="#94558d">cls</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>string_data):<br/>        year<span style="color: #cc7832"><font color="#cc7832">, </font></span>month<span style="color: #cc7832"><font color="#cc7832">, </font></span>day = <span style="color: #8888c6"><font color="#8888c6">map</font></span>(<span style="color: #8888c6"><font color="#8888c6">int</font></span><span style="color: #cc7832"><font color="#cc7832">,</font></span>string_data.split(<span style="color: #6a8759"><font color="#6a8759">'-'</font></span>))<br/>        data1 = <span style="color: #94558d"><font color="#94558d">cls</font></span>(year<span style="color: #cc7832"><font color="#cc7832">, </font></span>month<span style="color: #cc7832"><font color="#cc7832">, </font></span>day)<br/>        <span style="color: #cc7832"><font color="#cc7832">return </font></span>data1<br/><br/>a = Data_test()</pre>
  </body>
</html>
