    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Consolas; font-size: 12.0pt"><span style="color: #cc7832"><font color="#cc7832">public class </font></span>HomeTheaterTestDrive {<br/>   <span style="color: #cc7832"><font color="#cc7832">public static void </font></span><span style="color: #ffc66d"><font color="#ffc66d">main</font></span>(String[] args) {<br/>      Amplifier amp = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Amplifier(<span style="color: #6a8759"><font color="#6a8759">&quot;Top-O-Line Amplifier&quot;</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>Tuner tuner = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Tuner(<span style="color: #6a8759"><font color="#6a8759">&quot;Top-O-Line AM/FM Tuner&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>amp)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>DvdPlayer dvd = <span style="color: #cc7832"><font color="#cc7832">new </font></span>DvdPlayer(<span style="color: #6a8759"><font color="#6a8759">&quot;Top-O-Line DVD Player&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>amp)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>CdPlayer cd = <span style="color: #cc7832"><font color="#cc7832">new </font></span>CdPlayer(<span style="color: #6a8759"><font color="#6a8759">&quot;Top-O-Line CD Player&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>amp)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>Projector projector = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Projector(<span style="color: #6a8759"><font color="#6a8759">&quot;Top-O-Line Projector&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span>dvd)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>TheaterLights lights = <span style="color: #cc7832"><font color="#cc7832">new </font></span>TheaterLights(<span style="color: #6a8759"><font color="#6a8759">&quot;Theater Ceiling Lights&quot;</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>Screen screen = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Screen(<span style="color: #6a8759"><font color="#6a8759">&quot;Theater Screen&quot;</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>PopcornPopper popper = <span style="color: #cc7832"><font color="#cc7832">new </font></span>PopcornPopper(<span style="color: #6a8759"><font color="#6a8759">&quot;Popcorn Popper&quot;</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832"> <br/></span><span style="color: #cc7832">      </span></font>HomeTheaterFacade homeTheater = <span style="color: #cc7832"><font color="#cc7832">new </font></span>HomeTheaterFacade(<br/>            amp<span style="color: #cc7832"><font color="#cc7832">, </font></span>tuner<span style="color: #cc7832"><font color="#cc7832">, </font></span>dvd<span style="color: #cc7832"><font color="#cc7832">, </font></span>cd<span style="color: #cc7832"><font color="#cc7832">, </font></span>projector<span style="color: #cc7832"><font color="#cc7832">, </font></span>screen<span style="color: #cc7832"><font color="#cc7832">, </font></span>lights<span style="color: #cc7832"><font color="#cc7832">, </font></span>popper)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832"> <br/></span><span style="color: #cc7832">      </span></font>homeTheater.watchMovie(<span style="color: #6a8759"><font color="#6a8759">&quot;Raiders of the Lost Ark&quot;</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>homeTheater.endMovie()<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">   </span></font>}<br/>}</pre>
