t></span>)<br/>   <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">cook</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span>): <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;Cook Pizza&quot;</font></span>)<br/>   <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">eat</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span>): <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;Eat Pizza&quot;</font></span>)<br/><br/><span style="color: #cc7832"><font color="#cc7832">class </font></span>MakeTea(MakeMeal):<br/>   <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">prepare</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span>): <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;Prepare Tea&quot;</font></span>)<br/>   <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">cook</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span>): <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;Cook Tea&quot;</font></span>)<br/>   <span style="color: #cc7832"><font color="#cc7832">def </font></span><span style="color: #ffc66d"><font color="#ffc66d">eat</font></span>(<span style="color: #94558d"><font color="#94558d">self</font></span>): <span style="color: #8888c6"><font color="#8888c6">print</font></span>(<span style="color: #6a8759"><font color="#6a8759">&quot;Eat Tea&quot;</font></span>)<br/>