    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Consolas; font-size: 12.0pt"><span style="color: #cc7832"><font color="#cc7832">import </font></span>java.util.ArrayList<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">import </span></font>java.util.List<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">public class </span></font>Employee {<br/>    <span style="color: #cc7832"><font color="#cc7832">private </font></span>String <span style="color: #9876aa"><font color="#9876aa">name</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    private </span></font>String <span style="color: #9876aa"><font color="#9876aa">dept</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    private int </span></font><span style="color: #9876aa"><font color="#9876aa">salary</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    private </span></font>List&lt;Employee&gt; <span style="color: #9876aa"><font color="#9876aa">subordinates</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    </span></font><span style="color: #808080"><font color="#808080">//</font></span><span style="color: #808080; font-family: Arial"><font color="#808080" face="Arial">&#26500;&#36896;&#20989;&#25968;<br/></font></span><font color="#808080" face="Arial"><span style="color: #808080; font-family: Arial">    </span></font><span style="color: #cc7832"><font color="#cc7832">public </font></span><span style="color: #ffc66d"><font color="#ffc66d">Employee</font></span>(String name<span style="color: #cc7832"><font color="#cc7832">,</font></span>String dept<span style="color: #cc7832"><font color="#cc7832">, int </font></span>sal) {<br/>        <span style="color: #cc7832"><font color="#cc7832">this</font></span>.<span style="color: #9876aa"><font color="#9876aa">name </font></span>= name<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        this</span></font>.<span style="color: #9876aa"><font color="#9876aa">dept </font></span>= dept<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        this</span></font>.<span style="color: #9876aa"><font color="#9876aa">salary </font></span>= sal<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font><span style="color: #9876aa"><font color="#9876aa">subordinates </font></span>= <span style="color: #cc7832"><font color="#cc7832">new </font></span>ArrayList&lt;Employee&gt;()<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    </span></font>}<br/>    <span style="color: #cc7832"><font color="#cc7832">public void </font></span><span style="color: #ffc66d"><font color="#ffc66d">add</font></span>(Employee e) {<br/>        <span style="color: #9876aa"><font color="#9876aa">subordinates</font></span>.add(e)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    </span></font>}<br/>    <span style="color: #cc7832"><font color="#cc7832">public void </font></span><span style="color: #ffc66d"><font color="#ffc66d">remove</font></span>(Employee e) {<br/>        <span style="color: #9876aa"><font color="#9876aa">subordinates</font></span>.remove(e)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    </span></font>}<br/>    <span style="color: #cc7832"><font color="#cc7832">public </font></span>List&lt;Employee&gt; <span style="color: #ffc66d"><font color="#ffc66d">getSubordinates</font></span>(){<br/>        <span style="color: #cc7832"><font color="#cc7832">return </font></span><span style="color: #9876aa"><font color="#9876aa">subordinates</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">    </span></font>}<br/>}</pre>
