    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Consolas; font-size: 9.8pt"><span style="color: #cc7832"><font color="#cc7832">import </font></span>java.util.*<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">public class </span></font>SimpleSubject <span style="color: #cc7832"><font color="#cc7832">implements </font></span>Subject {<br/>   <span style="color: #cc7832"><font color="#cc7832">private </font></span>ArrayList&lt;Observer&gt; <span style="color: #9876aa"><font color="#9876aa">observers</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">   private int </span></font><span style="color: #9876aa"><font color="#9876aa">value </font></span>= <span style="color: #6897bb"><font color="#6897bb">0</font></span><span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">   public </span></font><span style="color: #ffc66d"><font color="#ffc66d">SimpleSubject</font></span>() {<br/>      <span style="color: #9876aa"><font color="#9876aa">observers </font></span>= <span style="color: #cc7832"><font color="#cc7832">new </font></span>ArrayList&lt;Observer&gt;()<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">   </span></font>}<br/>   <span style="color: #cc7832"><font color="#cc7832">public void </font></span><span style="color: #ffc66d"><font color="#ffc66d">registerObserver</font></span>(Observer o) {<br/>      <span style="color: #9876aa"><font color="#9876aa">observers</font></span>.add(o)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">   </span></font>}<br/>   <span style="color: #cc7832"><font color="#cc7832">public void </font></span><span style="color: #ffc66d"><font color="#ffc66d">removeObserver</font></span>(Observer o) {<br/>      <span style="color: #cc7832"><font color="#cc7832">int </font></span>i = <span style="color: #9876aa"><font color="#9876aa">observers</font></span>.indexOf(o)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      if </span></font>(i &gt;= <span style="color: #6897bb"><font color="#6897bb">0</font></span>) {<br/>         <span style="color: #9876aa"><font color="#9876aa">observers</font></span>.remove(i)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>}<br/>   }<br/>   <span style="color: #cc7832"><font color="#cc7832">public void </font></span><span style="color: #ffc66d"><font color="#ffc66d">notifyObservers</font></span>() {<br/>      <span style="color: #cc7832"><font color="#cc7832">for </font></span>(Observer observer : <span style="color: #9876aa"><font color="#9876aa">observers</font></span>) {<br/>         observer.update(<span style="color: #9876aa"><font color="#9876aa">value</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>}<br/>   }<br/>   <span style="color: #cc7832"><font color="#cc7832">public void </font></span><span style="color: #ffc66d"><font color="#ffc66d">setValue</font></span>(<span style="color: #cc7832"><font color="#cc7832">int </font></span>value) {<br/>      <span style="color: #cc7832"><font color="#cc7832">this</font></span>.<span style="color: #9876aa"><font color="#9876aa">value </font></span>= value<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">      </span></font>notifyObservers()<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">   </span></font>}<br/>}</pre>
  </body>
