    <pre style="background-color: #2b2b2b; color: #a9b7c6; font-family: Consolas; font-size: 12.0pt"><span style="color: #cc7832"><font color="#cc7832">public class </font></span>CompositePatternDemo {<br/>    <span style="color: #cc7832"><font color="#cc7832">public static void </font></span><span style="color: #ffc66d"><font color="#ffc66d">main</font></span>(String[] args) {<br/>        Employee CEO = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Employee(<span style="color: #6a8759"><font color="#6a8759">&quot;John&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">,</font></span><span style="color: #6a8759"><font color="#6a8759">&quot;CEO&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #6897bb"><font color="#6897bb">30000</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>Employee headSales = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Employee(<span style="color: #6a8759"><font color="#6a8759">&quot;Robert&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">,</font></span><span style="color: #6a8759"><font color="#6a8759">&quot;Head Sales&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #6897bb"><font color="#6897bb">20000</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>Employee headMarketing = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Employee(<span style="color: #6a8759"><font color="#6a8759">&quot;Michel&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">,</font></span><span style="color: #6a8759"><font color="#6a8759">&quot;Head Marketing&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #6897bb"><font color="#6897bb">20000</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>Employee salesExecutive1 = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Employee(<span style="color: #6a8759"><font color="#6a8759">&quot;Richard&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">,</font></span><span style="color: #6a8759"><font color="#6a8759">&quot;Sales&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #6897bb"><font color="#6897bb">10000</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>Employee salesExecutive2 = <span style="color: #cc7832"><font color="#cc7832">new </font></span>Employee(<span style="color: #6a8759"><font color="#6a8759">&quot;Rob&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">,</font></span><span style="color: #6a8759"><font color="#6a8759">&quot;Sales&quot;</font></span><span style="color: #cc7832"><font color="#cc7832">, </font></span><span style="color: #6897bb"><font color="#6897bb">10000</font></span>)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832"><br/></span><span style="color: #cc7832">        </span></font>CEO.add(headSales)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>CEO.add(headMarketing)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>headSales.add(salesExecutive1)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        </span></font>headSales.add(salesExecutive2)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832"><br/></span><span style="color: #cc7832">        </span></font><span style="color: #808080"><font color="#808080">//</font></span><span style="color: #808080; font-family: Arial"><font color="#808080" face="Arial">&#25171;&#21360;&#35813;&#32452;&#32455;&#30340;&#25152;&#26377;&#21592;&#24037;<br/></font></span><font color="#808080" face="Arial"><span style="color: #808080; font-family: Arial">        </span></font>System.<span style="color: #9876aa; font-style: italic"><font color="#9876aa"><i>out</i></font></span>.println(CEO)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">        for </span></font>(Employee headEmployee : CEO.getSubordinates()) {<br/>            System.<span style="color: #9876aa; font-style: italic"><font color="#9876aa"><i>out</i></font></span>.println(headEmployee)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">            for </span></font>(Employee employee : headEmployee.getSubordinates()) {<br/>                System.<span style="color: #9876aa; font-style: italic"><font color="#9876aa"><i>out</i></font></span>.println(employee)<span style="color: #cc7832"><font color="#cc7832">;<br/></font></span><font color="#cc7832"><span style="color: #cc7832">            </span></font>}<br/>        }<br/>    }<br/>}</pre>
